import React from 'react'

const defaultData = {
  current: null,
  colorScheme: 'green',
  defaultCurrent: 1,
  defaultPageSize: 10,
  disabled: false,
  hideOnSinglePage: false,
  itemRender: null,
  pageSize: null,
  pageSizeOptions: [10, 20, 50, 100],
  responsive: null,
  showLessItems: false,
  showQuickJumper: false,
  showSizeChanger: null,
  showTitle: true,
  showTotal: null,
  simple: null,
  total: 0,
  size: 'md',
  rounded: 'md',
  baseStyles: null,
  activeStyles: null,
  hoverStyles: null
}
const PaginationContext = React.createContext({
  ...defaultData,
  current: defaultData.defaultCurrent,
  pageSize: defaultData.defaultPageSize
})

export default PaginationContext
import React from 'react'

import { useColorModeValue, chakra } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'

export const PagButton = (props) => {
  const {
    colorScheme,
    rounded,
    size,
    baseStyles,
    activeStyles,
    hoverStyles
  } = React.useContext(PagContext)

  const activeStyle = activeStyles || {
    bg: useColorModeValue(`${colorScheme}.600`, `${colorScheme}.500`),
    color: useColorModeValue('white', 'gray.200')
  }
  const baseStyle = baseStyles || {
    rounded: rounded,
    bg: useColorModeValue('white', 'gray.800'),
    color: useColorModeValue('gray.700', 'gray.200'),
    opacity: props.disabled && 0.6
  }
  const sizeStyle = (size) => {
    var styles
    switch (size) {
      case 'xs':
        styles = { px: 2, fontSize: 'xs' }
        break

      case 'sm':
        styles = { px: 3, py: 1, fontSize: 'sm' }
        break

      case 'md':
        styles = { px: 4, py: 1, fontSize: 'md' }
        break

      case 'lg':
        styles = { px: 5, py: 2, fontSize: 'lg' }
        break

      default:
        break
    }
    return !baseStyles && styles
  }
  return (
    <chakra.a
      href='#'
      mx={1}
      {...sizeStyle(size)}
      {...baseStyle}
      _hover={!props.disabled && (hoverStyles || activeStyle)}
      {...(props.active && activeStyle)}
      cursor={props.disabled && 'not-allowed'}
    >
      {props.children}
    </chakra.a>
  )
}
import React from 'react'
import { PagButton } from './pbutton'
import { Flex } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'

export const PaginationComp = () => {
  const props = React.useContext(PagContext)

  const pagesLength = Math.ceil(props.total / props.pageSize)
  const buttons = []
  for (let i = 1; i <= pagesLength; i++) {
    buttons.push(
      <PagButton active={i === props.current} key={`page-${i}`}>
        {i}
      </PagButton>
    )
  }
  return (
    props.total > 0 && (
      <Flex>
        <PagButton disabled={props.current === 1}>◀️</PagButton>
        {buttons}
        <PagButton disabled={props.current === pagesLength}>▶️</PagButton>
      </Flex>
    )
  )
}
import React from 'react'

export const filterProps = (props) => {
  return React.useMemo(() => props, [
    props.current,
    props.colorScheme,
    props.defaultCurrent,
    props.defaultPageSize,
    props.disabled,
    props.hideOnSinglePage,
    props.itemRender,
    props.pageSize,
    props.pageSizeOptions,
    props.responsive,
    props.showLessItems,
    props.showQuickJumper,
    props.showSizeChanger,
    props.showTitle,
    props.showTotal,
    props.simple,
    props.total
  ])
}
import React from 'react'

import PagContext from './providers/pagination-provider'
import { PaginationComp } from './components/pagination'

import { filterProps } from './services/filter-props'
export const Pagination = (props) => {
  const globalProps = filterProps(props)
  const defaultProps = React.useContext(PagContext)
  return (
    <PagContext.Provider value={{ ...defaultProps, ...globalProps }}>
      <PaginationComp />
    </PagContext.Provider>
  )
}
