import { usePaginationStyles } from '../services/styles'
export const PagButton = (props) => {
  const paginationStyles = usePaginationStyles(props)
  return (
    <chakra.span mx={1} {...paginationStyles}>
      {props.children}
    </chakra.span>
  )
}
