import { PagButton } from './pbutton'
import { Flex } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'

export const PaginationComp = () => {
  const props = React.useContext(PagContext)

  const pagesLength = Math.ceil(props.total / props.pageSize)
  const buttons = []
  const prevRender = props.itemRender(props.current, 'prev')
  const nextRender = props.itemRender(props.current, 'next')

  if (!this.totalRecords || this.totalPages === 1) return null;

    const { currentPage } = this.state;
    const pages = this.fetchPageNumbers();

  return (
    props.total > 0 && (
      <Flex>
        <PagButton disabled={props.current === 1 || props.disabled}>
          {prevRender || '◀'}
        </PagButton>
        {buttons}
        <PagButton disabled={props.current === pagesLength || props.disabled}>
          {nextRender || '▶️'}
        </PagButton>
      </Flex>
    )
  )
}
import React from 'react'

export const filterProps = (props) => {
  return React.useMemo(() => props, [
    props.current,
    props.colorScheme,
    props.defaultCurrent,
    props.defaultPageSize,
    props.disabled,
    props.hideOnSinglePage,
    props.itemRender,
    props.pageSize,
    props.pageSizeOptions,
    props.responsive,
    props.showLessItems,
    props.showQuickJumper,
    props.showSizeChanger,
    props.showTitle,
    props.showTotal,
    props.simple,
    props.total
  ])
}
