import PagContext from './providers/pagination-provider'
import { PaginationComp } from './components/pagination'

import { filterProps } from './services/filter-props'
export const Pagination = (props) => {
  const globalProps = filterProps(props)
  const defaultProps = React.useContext(PagContext)
  const current =
    globalProps.current ||
    globalProps.defaultCurrent ||
    defaultProps.defaultCurrent
  const pageSize =
    globalProps.pageSize ||
    globalProps.defaultPageSize ||
    defaultProps.defaultPageSize
  return (
    <PagContext.Provider
      value={{ ...defaultProps, ...globalProps, current, pageSize }}
    >
      <PaginationComp />
    </PagContext.Provider>
  )
}
