import React from 'react'

const PaginationContext = React.createContext()

export default PaginationContext
import React from 'react'

import { useColorModeValue, chakra } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'

export const PagButton = (props) => {
  const { colorScheme } = React.useContext(PagContext)

  const activeStyle = {
    bg: useColorModeValue(`brand.600`, `brand.500`),
    color: useColorModeValue('white', 'gray.200')
  }
  return (
    <chakra.a
      href='#'
      mx={1}
      px={4}
      py={2}
      fontSize="md"
      borderRadius='md'
      bg={useColorModeValue('white', 'gray.800')}
      color={useColorModeValue('gray.700', 'gray.200')}
      opacity={props.disabled && 0.6}
      _hover={!props.disabled && activeStyle}
      cursor={props.disabled && 'not-allowed'}
      {...(props.active && activeStyle)}
    >
      {props.children}
    </chakra.a>
  )
}
import React from 'react'

import { Flex } from '@chakra-ui/react'

import { PagButton } from './pagination-buttons'
import PagContext from '../providers/pagination-provider'

export const Pagination = (props) => {
  const globalProps = React.useMemo(() => props, [props.colorScheme])
  return (
    <PagContext.Provider value={globalProps}>
      <Flex>
        <PagButton disabled>◀️</PagButton>
        <PagButton>1</PagButton>
        <PagButton active>2</PagButton>
        <PagButton>3</PagButton>
        <PagButton>4</PagButton>
        <PagButton>5</PagButton>
        <PagButton>▶️</PagButton>
      </Flex>
    </PagContext.Provider>
  )
}
