import React from 'react'

import { Pagination } from 'choc-paginator'
import 'choc-paginator/dist/index.css'
import { useColorMode, Button, Stack } from '@chakra-ui/react'

const Section = (props) => (
  <Stack
    spacing={5}
    bg='gray.600'
    p={10}
    w='full'
    direction='column'
    alignItems='center'
  >
    <h3>{props.title} </h3>
    {props.children}
  </Stack>
)
const App = () => {
  const { colorMode, toggleColorMode } = useColorMode()
  const itemRender = (_, type) => {
    if (type === 'prev') {
      return <span>Prev</span>
    }
    if (type === 'next') {
      return <span>Next</span>
    }
  }
  const [page, setPage] = React.useState(1)
  return (
    <div>
      <Section title=''>
        <Button onClick={toggleColorMode}>
          Toggle {colorMode === 'light' ? 'Dark' : 'Light'}
        </Button>
      </Section>
      <Section title='Basic Pagination'>
        <Pagination defaultCurrent={1} total={50} />
      </Section>
      <Section title='More Pages'>
        <Pagination
          defaultCurrent={19}
          total={500}
          itemRender={itemRender}
          pageNeighbours={2}
          colorScheme='red'
        />
      </Section>
      <Section title='Controlled Pages'>
        <Pagination current={page} total={50} />
      </Section>
      <Section title='Custom Text'>
        <Pagination defaultCurrent={2} total={50} itemRender={itemRender} />
      </Section>

      <Section title='Disabled Pagination'>
        <Pagination defaultCurrent={3} total={50} disabled />
      </Section>

      <Section title='Custom Styling'>
        <Pagination
          defaultCurrent={1}
          total={50}
          size='md'
          activeStyles={{ bg: 'yellow.800', cursor: 'pointer' }}
          baseStyles={{ bg: 'yellow.600', color: 'white', rounded: 'md' }}
        />
        <b>NB:</b>
        <li>The Styles are Chakra Style Props </li>
        <li>
          When hoverStyles prop is not present, activeStyles takes it place.
        </li>
      </Section>
    </div>
  )
}

export default App
