import React from 'react'

const defaultData = {
  current: null,
  colorScheme: 'green',
  defaultCurrent: 1,
  defaultPageSize: 10,
  disabled: false,
  hideOnSinglePage: false,
  itemRender: () => null,
  pageSize: null,
  pageNeighbours: 0, // indicates the number of additional page numbers to show on each side of the current page.
  pageSizeOptions: [10, 20, 50, 100],
  responsive: null,
  showLessItems: false,
  showQuickJumper: false,
  showSizeChanger: null,
  showTitle: true,
  showTotal: null,
  simple: null,
  total: 0,
  size: 'md',
  rounded: 'md',
  baseStyles: null,
  activeStyles: null,
  hoverStyles: null
}
const PaginationContext = React.createContext({
  ...defaultData
})

export default PaginationContext
import React from 'react'
import PagContext from '../providers/pagination-provider'

const LEFT_PAGE = 'LEFT'

const RIGHT_PAGE = 'RIGHT'

/**
 * Helper method for creating a range of numbers
 * range(1, 5) => [1, 2, 3, 4, 5]
 */
const range = (from, to, step = 1) => {
  let i = from
  const range = []

  while (i <= to) {
    range.push(i)
    i += step
  }

  return range
}

export const fetchPageNumbers = () => {
  const props = React.useContext(PagContext)
  const totalPages = Math.ceil(props.total / props.pageSize)
  const currentPage = props.curr
  const pageNeighbours = this.pageNeighbours

  /**
   * totalNumbers: the total page numbers to show on the control
   * totalBlocks: totalNumbers + 2 to cover for the left(<) and right(>) controls
   */
  const totalNumbers = this.pageNeighbours * 2 + 3
  const totalBlocks = totalNumbers + 2

  if (totalPages > totalBlocks) {
    const startPage = Math.max(2, currentPage - pageNeighbours)
    const endPage = Math.min(totalPages - 1, currentPage + pageNeighbours)
    let pages = range(startPage, endPage)

    /**
     * hasLeftSpill: has hidden pages to the left
     * hasRightSpill: has hidden pages to the right
     * spillOffset: number of hidden pages either to the left or to the right
     */
    const hasLeftSpill = startPage > 2
    const hasRightSpill = totalPages - endPage > 1
    const spillOffset = totalNumbers - (pages.length + 1)

    switch (true) {
      // handle: (1) < {5 6} [7] {8 9} (10)
      case hasLeftSpill && !hasRightSpill: {
        const extraPages = range(startPage - spillOffset, startPage - 1)
        pages = [LEFT_PAGE, ...extraPages, ...pages]
        break
      }

      // handle: (1) {2 3} [4] {5 6} > (10)
      case !hasLeftSpill && hasRightSpill: {
        const extraPages = range(endPage + 1, endPage + spillOffset)
        pages = [...pages, ...extraPages, RIGHT_PAGE]
        break
      }

      // handle: (1) < {4 5} [6] {7 8} > (10)
      case hasLeftSpill && hasRightSpill:
      default: {
        pages = [LEFT_PAGE, ...pages, RIGHT_PAGE]
        break
      }
    }

    return [1, ...pages, totalPages]
  }

  return range(1, totalPages)
}
import React from 'react'
import { PagButton } from './pbutton'
import { Flex } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'
import { fetchPageNumbers } from '../services/fetch-page'

export const PaginationComp = () => {
  const props = React.useContext(PagContext)

  const totalPages = Math.ceil(props.total / props.pageSize)
  const buttons = []
  const prevRender = props.itemRender(props.current, 'prev')
  const nextRender = props.itemRender(props.current, 'next')

  if (!totalPages || totalPages === 1) return null

  const pages = fetchPageNumbers()

  return (
    props.total > 0 && (
      <Flex>
        {pages.map((page, index) => {
          if (page === 'LEFT')
            return (
              <li key={index} className='page-item'>
                <a
                  className='page-link'
                  href='#'
                  aria-label='Previous'
                  onClick={this.handleMoveLeft}
                >
                  <span aria-hidden='true'>&laquo;</span>
                  <span className='sr-only'>Previous</span>
                </a>
              </li>
            )

          if (page === 'RIGHT')
            return (
              <li key={index} className='page-item'>
                <a
                  className='page-link'
                  href='#'
                  aria-label='Next'
                  onClick={this.handleMoveRight}
                >
                  <span aria-hidden='true'>&raquo;</span>
                  <span className='sr-only'>Next</span>
                </a>
              </li>
            )

          return (
            <li
              key={index}
              className={`page-item${currentPage === page ? ' active' : ''}`}
            >
              <a
                className='page-link'
                href='#'
                onClick={this.handleClick(page)}
              >
                {page}
              </a>
            </li>
          )
        })}
      </Flex>
    )
  )
}
import React from 'react'

export const filterProps = (props) => {
  return React.useMemo(() => props, [
    props.current,
    props.colorScheme,
    props.defaultCurrent,
    props.defaultPageSize,
    props.disabled,
    props.hideOnSinglePage,
    props.itemRender,
    props.pageSize,
    props.pageSizeOptions,
    props.responsive,
    props.showLessItems,
    props.showQuickJumper,
    props.showSizeChanger,
    props.showTitle,
    props.showTotal,
    props.simple,
    props.total
  ])
}
import React from 'react'

import PagContext from './providers/pagination-provider'
import { PaginationComp } from './components/pagination'

import { filterProps } from './services/filter-props'
export const Pagination = (props) => {
  const globalProps = filterProps(props)
  const defaultProps = React.useContext(PagContext)
  const current =
    globalProps.current ||
    globalProps.defaultCurrent ||
    defaultProps.defaultCurrent
  const pageSize =
    globalProps.pageSize ||
    globalProps.defaultPageSize ||
    defaultProps.defaultPageSize
  return (
    <PagContext.Provider
      value={{ ...defaultProps, ...globalProps, current, pageSize }}
    >
      <PaginationComp />
    </PagContext.Provider>
  )
}
