import PagContext from '../providers/pagination-provider'

const LEFT_PAGE = 'LEFT'

const RIGHT_PAGE = 'RIGHT'

/**
 * Helper method for creating a range of numbers
 * range(1, 5) => [1, 2, 3, 4, 5]
 */
const range = (from, to, step = 1) => {
  let i = from
  const range = []

  while (i <= to) {
    range.push(i)
    i += step
  }

  return range
}

export const fetchPageNumbers = () => {
  const props = React.useContext(PagContext)
  const totalPages = Math.ceil(props.total / props.pageSize)
  const currentPage = props.currentPage
  const pageNeighbours = props.pageNeighbours

  /**
   * totalNumbers: the total page numbers to show on the control
   * totalBlocks: totalNumbers + 2 to cover for the left(<) and right(>) controls
   */
  const totalNumbers = pageNeighbours * 2 + 3
  const totalBlocks = totalNumbers + 2

  if (totalPages > totalBlocks) {
    const startPage = Math.max(2, currentPage - pageNeighbours)
    const endPage = Math.min(totalPages - 1, currentPage + pageNeighbours)
    let pages = range(startPage, endPage)

    /**
     * hasLeftSpill: has hidden pages to the left
     * hasRightSpill: has hidden pages to the right
     * spillOffset: number of hidden pages either to the left or to the right
     */
    const hasLeftSpill = startPage > 2
    const hasRightSpill = totalPages - endPage > 1
    const spillOffset = totalNumbers - (pages.length + 1)

    switch (true) {
      // handle: (1) < {5 6} [7] {8 9} (10)
      case hasLeftSpill && !hasRightSpill: {
        const extraPages = range(startPage - spillOffset, startPage - 1)
        pages = [LEFT_PAGE, ...extraPages, ...pages]
        break
      }

      // handle: (1) {2 3} [4] {5 6} > (10)
      case !hasLeftSpill && hasRightSpill: {
        const extraPages = range(endPage + 1, endPage + spillOffset)
        pages = [...pages, ...extraPages, RIGHT_PAGE]
        break
      }

      // handle: (1) < {4 5} [6] {7 8} > (10)
      case hasLeftSpill && hasRightSpill:
      default: {
        pages = [LEFT_PAGE, ...pages, RIGHT_PAGE]
        break
      }
    }

    return [1, ...pages, totalPages]
  }

  return range(1, totalPages)
}
import React from 'react'
import { PagButton } from './pbutton'
import { Flex } from '@chakra-ui/react'
import PagContext from '../providers/pagination-provider'
import { fetchPageNumbers } from '../services/fetch-page'

export const PaginationComp = () => {
  const props = React.useContext(PagContext)

  const totalPages = Math.ceil(props.total / props.pageSize)

  const prevRender = props.itemRender(props.currentPage, 'prev')
  const nextRender = props.itemRender(props.currentPage, 'next')
  const leftRender = props.itemRender(props.currentPage, 'left')
  const rightRender = props.itemRender(props.currentPage, 'right')

  if (!totalPages || totalPages === 1) return null

  const pages = fetchPageNumbers()
  console.log('pages', pages)

  const changePage = (page) => {
    props.onChange(page, totalPages, props.pageSize, props.total)
    props.setCurrentPage(page)
  }

  const fastBackward = () => {
    const page = props.currentPage - props.pageNeighbours * 2 - 1
    props.onChange(page, props.pageSize, props.total)
    props.setCurrentPage(page)
  }

  const fastForward = () => {
    const page = props.currentPage + props.pageNeighbours * 2 + 1
    props.onChange(page, props.pageSize, props.total)
    props.setCurrentPage(page)
  }

  const backward = () => {
    props.onChange(props.currentPage - 1, props.pageSize, props.total)
    props.setCurrentPage(props.currentPage - 1)
  }

  const forward = () => {
    props.onChange(props.currentPage + 1, props.pageSize, props.total)
    props.setCurrentPage(props.currentPage + 1)
  }
  return (
    props.total > 0 && (
      <Flex>
        <PagButton
          disabled={
            (props.current || props.currentPage) === 1 || props.disabled
          }
          onClick={() => backward()}
        >
          {prevRender || '◀'}
        </PagButton>
        {pages.map((page, index) => {
          if (page === 'LEFT')
            return (
              <PagButton key={index} onClick={() => fastBackward()}>
                {leftRender || '<<'}
              </PagButton>
            )

          if (page === 'RIGHT')
            return (
              <PagButton key={index} onClick={() => fastForward()}>
                {rightRender || '>>'}
              </PagButton>
            )

          return (
            <PagButton
              disabled={props.disabled}
              active={page === (props.current || props.currentPage)}
              key={`page-${index}`}
              onClick={() => changePage(page)}
            >
              {page}
            </PagButton>
          )
        })}
        <PagButton
          disabled={
            (props.current || props.currentPage) === totalPages ||
            props.disabled
          }
          onClick={() => forward()}
        >
          {nextRender || '▶️'}
        </PagButton>
      </Flex>
    )
  )
}
